import CourseCard from './components/CourseCard/CourseCard';
import { mockedCoursesList, mockedAuthorsList } from '../../constants';

function Courses() {
	const course1 = mockedCoursesList[0];
	const authorNames = mockedAuthorsList
		.filter((item) => {
			//console.log(course1.authors);
			return course1.authors.includes(item.id);
		})
		.map((item) => {
			console.log(item.name);
			return item.name;
		})
		.join(', ');
	console.log(authorNames);
	const time_convert = (num) => {
		const hours = Math.floor(num / 60);
		const minutes = num % 60;
		return hours + ':' + minutes;
	};

	return (
		<div className='courses'>
			<CourseCard
				title={course1.title}
				authorsNames={authorNames}
				cardDescription={course1.description}
				creationDate={course1.creationDate}
				duration={time_convert(course1.duration)}
			/>
		</div>
	);
}

export default Courses;
