//import { useState } from 'react';
import Button from '../../common/Button/Button';
import Input from '../../common/Input/Input';
import { mockedAuthorsList } from '../../constants';
import './CreateCourse.style.css';

function CreateCourse({ setAddCourse }) {
	let courseAuthors = [];
	const listAllAuthors = (authorsList, btnText) => {
		return authorsList.map((author) => (
			<div key={author.id} className='author'>
				<span>{author.name}</span>
				<Button text={btnText} onClick={() => {}} />
			</div>
		));
	};
	const convertDuration = (num) => {
		const hours = Math.floor(num / 60);
		const minutes = num % 60;
		return hours + ':' + minutes + ' hours';
	};
	return (
		<form className='createCourse'>
			<div className='titleSection'>
				<Input
					lblText='Title:'
					type='text'
					placeholderText='Enter title...'
					onChange={() => {}}
					required
				/>
				<Button text='Create course' onClick={() => {}} />
			</div>
			<div className='descriptionSection'>
				<label htmlFor='description'>Description:</label>
				<textarea
					name='description'
					id='description'
					placeholder='Enter description'
					cols='40'
					rows='6'
					value={''}
					onChange={() => {}}
					required
				/>
			</div>
			<section className='authorDurationSection'>
				<div className='addAuthor'>
					<h3>Add Author</h3>
					<Input
						lblText='Author name:'
						type='text'
						placeholderText='Enter author name...'
						onChange={() => {}}
						required
					/>
					<Button text='Create author' onClick={() => {}} />
				</div>
				<div className='authors'>
					<h3>Authors</h3>
					{listAllAuthors(mockedAuthorsList, 'Add author')}
				</div>
				<div className='duration'>
					<h3>Duration</h3>
					<Input
						lblText='Duration:'
						type='text'
						placeholderText='Enter duration in minutes...'
						onChange={() => {}}
						value={''}
						min='1'
						required
					/>
					<p className='convertDuration'>Duration: {convertDuration(100)}</p>
				</div>
				<div className='courseAuthors'>
					<h3>Course Authors</h3>
					{!courseAuthors.length ? (
						<p>Author list is empty</p>
					) : (
						listAllAuthors(courseAuthors, 'Delete author')
					)}
				</div>
			</section>
		</form>
	);
}

export default CreateCourse;
