import CourseCard from './components/CourseCard/CourseCard';
import { mockedCoursesList, mockedAuthorsList } from '../../constants';
import SearchBar from './components/SearchBar/SearchBar';
import { useState, useEffect } from 'react';

import './Courses.style.css';

function Courses() {
	const [courses, getCourses] = useState([]);
	const [searchPhrase, setSearchPhrase] = useState('');

	useEffect(() => {
		getCourses(mockedCoursesList);
	}, []);

	const handleSeachChange = (e) => {
		const phrase = e.target.value.trim();
		if (!phrase) {
			getCourses(mockedCoursesList);
		}
		setSearchPhrase(phrase);
	};

	const filterCourses = (searchPhrase) => {
		return mockedCoursesList.filter(
			({ id, title }) =>
				id.toLowerCase().includes(searchPhrase.toLowerCase()) ||
				title.toLowerCase().includes(searchPhrase.toLowerCase())
		);
	};

	const handleSubmit = (e) => {};
	const coursesList = courses.map((course) => {
		const authorNames = (course) => {
			return mockedAuthorsList
				.filter((item) => course.authors.includes(item.id))
				.map((item) => item.name)
				.join(', ');
		};
		const time_convert = (num) => {
			const hours = Math.floor(num / 60);
			const minutes = num % 60;
			return hours + ':' + minutes;
		};
		return (
			<CourseCard
				key={course.id}
				{...course}
				authorsNames={authorNames(course)}
				duration={time_convert(course.duration)}
			/>
		);
	});
	return (
		<>
			<div className='courses'>
				<SearchBar />
				{coursesList}
			</div>
		</>
	);
}

export default Courses;
